mapper.update:
update tb_user
set photo=#{url}
where id = #{userId}

mapper.select:
select if(count(*), true, false)
from tb_user
where tel = #{phone}

select * from tb_user


    nickname=#{form.name}


    and status=#{form.status}

SELECT d.id AS deptId, d.dept_name AS deptName, u.id AS userId, u.name FROM tb_dept d JOIN tb_user u ON u.dept_id=d.id WHERE u.status=1
AND u.name LIKE '%${keyword}%'
ORDER BY d.id, u.id;

SELECT id
FROM tb_user
WHERE nickname = #{name}
